// Validate required fields
const data = $input.first().json;

const required = ['title', 'description', 'price', 'files'];
const missing = required.filter(field => !data[field]);

if (missing.length > 0) {
  throw new Error(`Missing required fields: ${missing.join(', ')}`);
}

return {
  json: {
    title: data.title,
    description: data.description,
    price: parseFloat(data.price) || 9.99,
    author: data.author || 'Anonymous',
    keywords: data.keywords || [],
    categories: data.categories || [],
    files: data.files,
    platforms: data.platforms || ['gumroad', 'email'],
    coverImage: data.coverImage || null,
    distributionDate: new Date().toISOString()
  }
};

const response = $input.first().json;
const productId = response.product?.id || 'unknown';
const productUrl = response.product?.short_url || response.product?.url || 'N/A';

return {
  json: {
    platform: 'gumroad',
    status: 'published',
    productId: productId,
    productUrl: productUrl,
    title: $input.first().json.title,
    price: $input.first().json.price,
    publishedAt: new Date().toISOString()
  }
};

// Amazon KDP doesn't have a public API for direct uploads
// This node provides instructions for manual upload or API integration

const title = $input.first().json.title;
const description = $input.first().json.description;
const keywords = $input.first().json.keywords.join(', ');
const categories = $input.first().json.categories.join(', ');

const instructions = {
  platform: 'amazon-kdp',
  status: 'ready-for-manual-upload',
  message: 'Amazon KDP requires manual upload through their dashboard',
  steps: [
    '1. Go to https://kdp.amazon.com',
    '2. Click "Create New Title"',
    '3. Select "eBook"',
    `4. Enter title: ${title}`,
    `5. Enter description: ${description}`,
    `6. Add keywords: ${keywords}`,
    `7. Select categories: ${categories}`,
    '8. Upload manuscript file (EPUB or MOBI)',
    '9. Upload cover image',
    '10. Set pricing and territories',
    '11. Publish'
  ],
  files: $input.first().json.files,
  metadata: {
    title: title,
    description: description,
    keywords: keywords,
    categories: categories,
    price: $input.first().json.price,
    author: $input.first().json.author
  },
  note: 'Consider using Amazon KDP API or third-party services like PublishDrive for automation'
};

return { json: instructions };

return {
  json: {
    platform: 'email',
    status: 'sent',
    recipients: $input.first().json.emailList || 'subscriber list',
    title: $input.first().json.title,
    sentAt: new Date().toISOString()
  }
};

