const results = $input.all();

const distribution = {
  title: results[0].json.title,
  status: 'distributed',
  distributionDate: new Date().toISOString(),
  platforms: results.map(r => ({
    platform: r.json.platform,
    status: r.json.status,
    url: r.json.productUrl || r.json.url || null,
    publishedAt: r.json.publishedAt || r.json.sentAt || r.json.preparedAt
  })),
  summary: {
    total: results.length,
    successful: results.filter(r => r.json.status === 'published' || r.json.status === 'sent').length,
